import React, { useState } from 'react';
import PropTypes from 'prop-types'

export default function Textform(props) {
  return (
    <>  
    const[text,setText]=useSt
    <div className="mb-3 ">
    <h1>{props.heading}</h1>
     <textarea className="form-control" id="my-box" rows="8"></textarea>
    </div>
    <button className="btn btn-primary">Change To UpperCase</button>
    </> 
)
}
Textform.propType={title:PropTypes.string}
