import './App.css';
// import About from './components/About';
import Navbar from './components/Navbar'
import Textform from './components/Textform';
import React, { useState } from 'react';

function App() {
  const [mode, setMode] = useState('dark bg-primary');
  const toggle=()=>{
    if(mode==='dark bg-primary'){
      setMode('dark');
      document.body.style.backgroundColor='#040420';
    }else{
      setMode('dark bg-primary');
      document.body.style.backgroundColor='white';
    }
  }
  return (
   <>
    <Navbar title="TextUtils" mode={mode} toggle={toggle}/>
    <div className="container my-3">
      <Textform heading ="Enter Your Text Below to Analyze" mode={mode} toggle={toggle}/>
    </div>
    {/* <div className="container my-3">
      <About/>
    </div> */}
    </>
  );
}

export default App;
